		opt.maxTime = opt.maxTime.split(':');

		if (!((opt.minTime[0] >= 0) && (opt.minTime[0] < 24))) {
			opt.minTime[0] = "00";
		}
		if (!((opt.maxTime[0] >= 0) && (opt.maxTime[0] < 24))) {
			opt.maxTime[0] = "23";
		}
